import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import api from "../../Services/api";
import { Link } from 'react-router-dom';
import { FiArrowLeft, FiTrash2 } from "react-icons/fi";
import "./style.css";

export default function BooksUser() {
    const [booksUser, setBooks] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchBooks() {
            try {
                const books = localStorage.getItem('id');
                console.log(books);
                const response = await api.get(`/api/Book/${books}`);
                setBooks(response.data.data);
                console.log(response.data.data);
            } catch (error) {
                console.error(error);
            }
        }
        fetchBooks();
    }, []);

    return (
        <div className="container-books-user">
            <div className="secao-books-user">
                <h1>Meus Livros</h1>
            </div>
            <div className="livros-usuario">
                {booksUser.map(book => (
                    <div key={book.id} className="book-card">
                        <h2>{book.title}</h2>
                        <p>{book.author}</p>
                        <p>{book.description}</p>
                        <p>{book.genre}</p>
                        <p>{book.isbn}</p>
                        <p>{book.publisher}</p>
                        <p>{book.yearPublication}</p>
                        <p>{book.numberPages}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}
