import React from "react";
import { useNavigate } from 'react-router-dom';
import "./style.css";

export default function NewBook() {

    const [title, setTitle] = React.useState('');
    const [author, setAuthor] = React.useState('');
    const [description, setDescription] = React.useState('');
    const [genre, setGenre] = React.useState('');
    const [isbn, setIsbn] = React.useState('');
    const [publisher, setPublisher] = React.useState('');
    const [publicationYear, setPublicationYear] = React.useState('');
    const [numberOfPages, setNumberOfPages] = React.useState('');

    async function CreateNewBook(event) {
        event.preventDefault();

        const data = {
            title,
            author,
            description,
            genre,
            isbn,
            publisher,
            publicationYear,
            numberOfPages,
        };

        try {
            const response = await api.post('/books', data);
            console.log(response.data);
            alert('Livro criado com sucesso!');
        } catch (error) {
            console.error(error);
        }
    }

        

    return (
        <body>
            <header className="header">
                <h3>Sistema de avaliação de livros</h3>
            </header>
            <main>
                <div className="container">
                    <h1>Novo Livro</h1>
                    <div className="secao-form">
                        <form className="form" onSubmit={CreateNewBook}>
                            <input type="text" placeholder="Título" value={title} />
                            <input type="text" placeholder="Autor" value={author} />
                            <input type="text" placeholder="Descrição" value={description} />
                            <select value={genre} onChange={(e) => setGenre(e.target.value)}>
                                <option value="">Selecione um gênero</option>
                                <option value="Romance">Romance</option>
                                <option value="Comedy">Comedy</option>
                                <option value="Horror">Horror</option>
                                <option value="ScienceFiction">ScienceFiction</option>
                                <option value="Fantasy">Fantasy</option>
                                <option value="Documentary">Documentary</option>
                            </select>
                            <input type="text" placeholder="ISBN" value={isbn} />
                            <input type="text" placeholder="Editora" value={publisher} />
                            <input type="text" placeholder="Ano da pulicação" value={publicationYear} />
                            <input type="number" placeholder="Numero de paginas" value={numberOfPages} />

                            <button type="submit">Salvar</button>
                        </form>
                    </div>

                </div>
            </main>
            <footer className="footer">
                <h3>Desenvolvido por: Fabio dos Santos</h3>
            </footer>
        </body>

    );
}
